function isValidPhoneNumber(phoneNumber) {
  const phonePattern = /^[0-9\-\/*+]+$/;
  return phonePattern.test(phoneNumber);
}

let nama = document.querySelector("#inputUsername");
let email = document.querySelector("#inputEmail");
let phone = document.querySelector("#inputPhone");
let message = document.querySelector("#inputMessage");
let button = document.querySelector(".btn.btn-primary.px-4.py-3");

button.addEventListener("click", function () {
  let isValid = true;

  if (nama.value == "") {
    alert("Make sure your name is not empty!");
    isValid = false;
  }

  if (email.value == "") {
    alert("Make sure your email is not empty!");
    isValid = false;
  }

  if (phone.value == "") {
    alert("Make sure your telephone number is not empty!");
    isValid = false;
  } else if (!isValidPhoneNumber(phone.value)) {
    alert("Phone number should contain only digits and valid operators.");
    isValid = false;
  }

  if (message.value == "") {
    alert("Make sure your message is not empty!");
    isValid = false;
  }

  if (isValid) {
    alert("Message Sent!");
  }
});
